const { getHeroById } = require("./bases/08-importaciones-expo");


const promesa = new Promise( (resolve, reject) => { 
    setTimeout( () => {

        // tarea
        //Importar get heroe by id 
        const heroe = getHeroById(6);
        resolve(heroe)
        reject('No se encontro el heroe')
    },2000 )
 } );

promesa.then( 
    (heroe) => {
        console.log(heroe)
    }
)
.catch( err => console.warn(err) )
