const { getHeroById } = require("./bases/08-importaciones-expo");


const promesa = new Promise( (resolve, reject) => { 
    setTimeout( () => {

        // tarea
        //Importar get heroe by id 
        const heroe = getHeroById(2);
        resolve(heroe)
    },2000 )
 } );

promesa.then( 
    () => {
        console.log(heroe)
    }
)
